export function pluralize(word, count) {
    return `${word}${count === 1 ? '' : 's'}`;
}
export function reportBugURL(title) {
    return `https://github.com/xataio/client-ts/issues/new?labels=bug&template=bug_report.md&title=${encodeURIComponent(title)}`;
}
export const isNil = (value) => value === null || value === undefined;
